  (int?,Task?) findById(String id){
    for (int i = 0; i < myTasks.length; i++){
      if (myTasks[i].id == id){
        return (i,myTasks[i]);
      }
    }
    return (null,null);
  }
  void deleteById(String id){
    int index = -1;
    for (int i = 0; i < myTasks.length; i++){
      if (myTasks[i].id == id){
        index = i;
      }
    }
    if (index != -1){
      myTasks.removeAt(index);
    }
  }
